'use client'

import Image from 'next/image'
import { useState } from 'react'

interface RecipeImageProps {
  src: string
  alt: string
  width: number
  height: number
  className?: string
  priority?: boolean
}

const FALLBACK_IMAGES = {
  'appetizers': 'data:image/svg+xml;base64,',
  'main-course': 'data:image/svg+xml;base64,',
  'pasta': 'data:image/svg+xml;base64,',
  'salads': 'data:image/svg+xml;base64,',
  'soups': 'data:image/svg+xml;base64,',
  'desserts': 'data:image/svg+xml;base64,',
  'asian-fusion': 'data:image/svg+xml;base64,',
  'quick-meals': 'data:image/svg+xml;base64,',
  'default': 'data:image/svg+xml;base64,'
}

// Helper function to get category from image URL or recipe data
const getCategoryFromContext = (src: string, alt: string): string => {
  const lowerAlt = alt.toLowerCase()
  if (lowerAlt.includes('appetizer') || lowerAlt.includes('starter')) return 'appetizers'
  if (lowerAlt.includes('main') || lowerAlt.includes('entrÃ©e')) return 'main-course'
  if (lowerAlt.includes('pasta') || lowerAlt.includes('noodle')) return 'pasta'
  if (lowerAlt.includes('salad')) return 'salads'
  if (lowerAlt.includes('soup')) return 'soups'
  if (lowerAlt.includes('dessert') || lowerAlt.includes('sweet')) return 'desserts'
  if (lowerAlt.includes('asian') || lowerAlt.includes('thai') || lowerAlt.includes('chinese')) return 'asian-fusion'
  if (lowerAlt.includes('quick') || lowerAlt.includes('fast')) return 'quick-meals'
  return 'default'
}

export default function RecipeImage({ 
  src, 
  alt, 
  width, 
  height, 
  className = '', 
  priority = false 
}: RecipeImageProps) {
  const [imgSrc, setImgSrc] = useState(src)
  const [hasError, setHasError] = useState(false)

  const handleError = () => {
    if (!hasError) {
      setHasError(true)
      const category = getCategoryFromContext(src, alt)
      setImgSrc(FALLBACK_IMAGES[category as keyof typeof FALLBACK_IMAGES] || FALLBACK_IMAGES.default)
    }
  }

  return (
    <Image
      src={imgSrc}
      alt={alt}
      width={width}
      height={height}
      className={className}
      priority={priority}
      onError={handleError}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  )
}